import './App.css';
import { useState } from 'react';
import Input from './components/Input/Input';

function App() {
  const [list, setList] = useState([]);

  const handleAddItem = (text) => {
    setList((prevList) => [...prevList, text]);
  };
  return (
    <div className="App">
      <h1>Weather App</h1>
      <Input onAdd={handleAddItem} />
      <ul>
        {list.map((item, index) => (
          <li key={index}>

          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
